import React, { useState } from "react";
import { Token } from "./abi/abi";
import Web3 from "web3";
import './App.css';

// Access our wallet inside of our dapp
const web3 = new Web3(Web3.givenProvider);
// Contract address of the deployed smart contract
const contractAddress = "0xE422254A424fd7b25f8446891b722feB2E4544f6";
const tokenContract = new web3.eth.Contract(Token, contractAddress);

function App() {
  // Hold variables that will interact with our contract and frontend
  const [number, setUint] = useState(0);
  const [getNumber, setGet] = useState("0");

  const purchase = async (t) => {
    t.preventDefault();
    const accounts = await window.ethereum.enable();
    const account = accounts[0];
    // Get permission to access user funds to pay for gas fees
    const post = await tokenContract.methods.deposit().send({
      from: account,
      value: number,
    });
  };

  return (
     <div className="main">
       <div className="card">
         <form className="form" onSubmit={purchase}>
           <label>
             Purchase Token (input ETH amount)
             <input
               className="input"
               type="text"
               name="name"
               onChange={(t) => setUint(t.target.value)}
             />
           </label>
           <button className="button" type="submit" value="Confirm">
             Deposit
           </button>
         </form>

         <form className="form" onSubmit={purchase}>
           <label>
             Set BlackList (input address)
             <input
               className="input"
               type="text"
               name="name"
               onChange={(t) => setUint(t.target.value)}
             />
           </label>
           <button className="button" type="submit" value="Confirm">
             Confirm
           </button>
         </form>

         <form className="form" onSubmit={purchase}>
           <label>
             Set WhiteList (input address)
             <input
               className="input"
               type="text"
               name="name"
               onChange={(t) => setUint(t.target.value)}
             />
           </label>
           <button className="button" type="submit" value="Confirm">
             Confirm
           </button>
         </form>

         <form className="form" onSubmit={purchase}>
           <label>
             Transfer (input address, amount)
             <input
               className="input"
               type="text"
               name="name"
               onChange={(t) => setUint(t.target.value)}
             />
             <input
               className="input"
               type="text"
               name="name"
               onChange={(t) => setUint(t.target.value)}
             />
           </label>
           <button className="button" type="submit" value="Confirm">
             Confirm
           </button>
         </form>

       </div>
     </div>
  );
}

export default App;
