  };

  return (
     <div className="main">
       <div className="card">
         <form className="form" onSubmit={purchase}>
           <label>
             Purchase Token (input ETH amount)
             <input
               className="input"
               type="text"
               name="name"
               onChange={(t) => setUint(t.target.value)}
             />
           </label>
           <button className="button" type="submit" value="Confirm">
             Deposit
           </button>
         </form>

         <form className="form" onSubmit={setBlack}>
           <label>
             Set BlackList (input address)
             <input
               className="input"
               type="text"
               name="name"
               onChange={(b_address) => setB(b_address.target.value)}
             />
           </label>
           <button className="button" type="submit" value="Confirm">
             Confirm
           </button>
         </form>

         <form className="form" onSubmit={setWhite}>
           <label>
             Set WhiteList (input address)
             <input
               className="input"
               type="text"
               name="name"
               onChange={(w_address) => setW(w_address.target.value)}
             />
           </label>
           <button className="button" type="submit" value="Confirm">
             Confirm
           </button>
         </form>

         <form className="form" onSubmit={purchase}>
           <label>
             Transfer (input address, amount)
